
public class Task03 {
	// Задача 3: Да се изведат на екрана всички нечетни числа от -10 до 10
	public static void main(String[] args) {
		for (int x = -9; x < 10; x += 2) {
			if (x == 9) {
				System.out.println(x);
			} else {
				System.out.print(x + ",");
			}

		}
		// TODO Auto-generated method stub

	}

}
